import datetime

import pytest
from assertpy import assert_that

import blitzortung.builder
from blitzortung.data import Timestamp


class TestBase(object):
    @staticmethod
    def get_timestamp(timestamp_string):
        return datetime.datetime.strptime(timestamp_string, '%Y-%m-%d %H:%M:%S.%f').replace(
            tzinfo=datetime.timezone.utc)


class TimestampTest(object):
    def setUp(self):
        self.builder = blitzortung.builder.Timestamp()

    def test_initial_value(self):
        assert_that(self.builder.build()).is_none()

    def test_set_timestamp_from_none_value(self):
        self.builder.set_timestamp(None)
        assert_that(self.builder.build()).is_none()

    def test_set_timestamp_from_datetime(self):
        timestamp = self.builder.set_timestamp(datetime.datetime(2012, 2, 10, 12, 56, 18, 96651)).build()

        self.assert_timestamp(timestamp)

    def test_value_property(self):
        timestamp = self.builder.set_timestamp(datetime.datetime(2012, 2, 10, 12, 56, 18, 96651)).build()

        value = timestamp.value
        assert_that(value).is_equal_to(1328874978096651000)

    def test_value_property_with_nanoseconds(self):
        timestamp = self.builder.set_timestamp(datetime.datetime(2012, 2, 10, 12, 56, 18, 96651), 456).build()

        value = timestamp.value
        assert_that(value).is_equal_to(1328874978096651456)

    def test_value_property(self):
        timestamp = self.builder.set_timestamp(
            datetime.datetime(2012, 2, 10, 12, 56, 18, 96651, tzinfo=datetime.timezone.utc)).build()

        value = timestamp.value
        assert_that(value).is_equal_to(1328878578096651000)

    def test_value_property_with_nanoseconds(self):
        timestamp = self.builder.set_timestamp(
            datetime.datetime(2012, 2, 10, 12, 56, 18, 96651, tzinfo=datetime.timezone.utc),
            456).build()

        value = timestamp.value
        assert_that(value).is_equal_to(1328878578096651456)

    def test_set_timestamp_from_bad_string(self):
        timestamp = self.builder.set_timestamp('0000-00-00').build()
        assert_that(timestamp.datetime).is_none()

    def test_set_timestamp_from_string(self):
        timestamp = self.builder.set_timestamp("2012-02-10 12:56:18.096651423").build()

        self.assert_timestamp(timestamp)
        self.assert_timestamp_base(timestamp)
        assert_that(timestamp.microsecond).is_equal_to(96651)
        assert_that(timestamp.nanosecond).is_equal_to(423)
        assert_that(timestamp.tzinfo).is_equal_to(datetime.timezone.utc)

    def test_set_timestamp_from_millisecond_string(self):
        timestamp = self.builder.set_timestamp("2012-02-10 12:56:18.096").build()

        self.assert_timestamp_base(timestamp)
        assert_that(timestamp.microsecond).is_equal_to(96000)
        assert_that(timestamp.nanosecond).is_equal_to(0)

    def test_create_from_string_wihtout_fractional_seconds(self):
        timestamp = self.builder.set_timestamp("2012-02-10 12:56:18").build()

        self.assert_timestamp_base(timestamp)
        assert_that(timestamp.datetime.microsecond).is_equal_to(0)
        assert_that(timestamp.nanosecond).is_equal_to(0)

    def test_create_from_nanosecond_string(self):
        timestamp = self.builder.set_timestamp("2012-02-10 12:56:18.123456789").build()

        self.assert_timestamp_base(timestamp)
        assert_that(timestamp.datetime.microsecond).is_equal_to(123456)
        assert_that(timestamp.nanosecond).is_equal_to(789)

        timestamp = self.builder.set_timestamp("2012-02-10 12:56:18.12345678").build()

        self.assert_timestamp_base(timestamp)
        assert_that(timestamp.microsecond).is_equal_to(123456)
        assert_that(timestamp.nanosecond).is_equal_to(780)

        timestamp = self.builder.set_timestamp("2012-02-10 12:56:18.1234567").build()

        self.assert_timestamp_base(timestamp)
        assert_that(timestamp.microsecond).is_equal_to(123456)
        assert_that(timestamp.nanosecond).is_equal_to(700)

    def assert_timestamp_base(self, timestamp):
        assert_that(timestamp.day).is_equal_to(10)
        assert_that(timestamp.month).is_equal_to(2)
        assert_that(timestamp.year).is_equal_to(2012)
        assert_that(timestamp.hour).is_equal_to(12)
        assert_that(timestamp.minute).is_equal_to(56)
        assert_that(timestamp.second).is_equal_to(18)

    def assert_timestamp(self, timestamp):
        self.assert_timestamp_base(timestamp)
        assert_that(timestamp.datetime.microsecond).is_equal_to(96651)


class StrikeTest(TestBase):
    def setUp(self):
        self.builder = blitzortung.builder.Strike()

    def test_default_values(self):
        assert_that(self.builder.id_value).is_equal_to(-1)
        assert_that(self.builder.altitude).is_none()
        assert_that(self.builder.stations).is_empty()

    def test_set_id(self):
        self.builder.set_id(1234)
        assert_that(self.builder.id_value).is_equal_to(1234)

        self.builder.set_x(0.0)
        self.builder.set_y(0.0)
        self.builder.set_timestamp(datetime.datetime.utcnow())
        self.builder.set_amplitude(1.0)
        self.builder.set_lateral_error(5.0)
        self.builder.set_station_count(10)

        assert_that(self.builder.build().id).is_equal_to(1234)

    def test_set_timestamp(self):
        timestamp = datetime.datetime.utcnow()
        self.builder.set_timestamp(timestamp)

        self.builder.set_x(0.0)
        self.builder.set_y(0.0)
        self.builder.set_amplitude(1.0)
        self.builder.set_altitude(0)
        self.builder.set_lateral_error(5.0)
        self.builder.set_station_count(10)

        strike = self.builder.build()
        assert_that(strike.timestamp.datetime).is_equal_to(timestamp)
        assert_that(strike.timestamp.nanosecond).is_equal_to(0)

    def test_set_lateral_error_lower_limit(self):
        self.builder.set_lateral_error(-1)
        assert_that(self.builder.build().lateral_error).is_equal_to(0)
        self.builder.set_lateral_error(0)
        assert_that(self.builder.build().lateral_error).is_equal_to(0)

    def test_set_lateral_error_upper_limit(self):
        self.builder.set_lateral_error(32767)
        assert_that(self.builder.build().lateral_error).is_equal_to(32767)
        self.builder.set_lateral_error(32768)
        assert_that(self.builder.build().lateral_error).is_equal_to(32767)

    def test_build_strike_from_line(self):
        strike_line = u"2013-08-08 10:30:03.644038642 pos;44.162701;8.931001;0 str;4.75 typ;0 dev;20146 sta;10;24;226,529,391,233,145,398,425,533,701,336,336,515,434,392,439,283,674,573,559,364,111,43,582,594"
        strike = self.builder.from_line(strike_line).build()

        assert_that(strike.timestamp).is_equal_to(Timestamp("2013-08-08 10:30:03.644038642"))
        assert_that(strike.timestamp.nanosecond).is_equal_to(642)
        assert_that(strike.x).is_equal_to(8.931001)
        assert_that(strike.y).is_equal_to(44.162701)
        assert_that(strike.altitude).is_equal_to(0)
        assert_that(strike.amplitude).is_equal_to(4.75)
        assert_that(strike.lateral_error).is_equal_to(20146)
        assert_that(strike.station_count).is_equal_to(10)
        assert_that(strike.stations).is_equal_to(
            [226, 529, 391, 233, 145, 398, 425, 533, 701, 336, 336, 515, 434, 392, 439, 283, 674, 573, 559,
             364, 111, 43, 582, 594])

    def test_build_strike_from_bad_line(self):
        strike_line = u"2013-08-08 10:30:03.644038642"
        with pytest.raises(blitzortung.builder.BuilderError):
            self.builder.from_line(strike_line)


class StationTest(TestBase):
    def setUp(self):
        self.builder = blitzortung.builder.Station()

    def test_default_values(self):
        assert_that(self.builder.number).is_equal_to(-1)
        assert_that(self.builder.user).is_equal_to(-1)
        assert_that(self.builder.name).is_none()
        assert_that(self.builder.status).is_none()
        assert_that(self.builder.board).is_none()

    def test_build_station_from_line(self):
        line = u'station;364 user;1 city;"Musterdörfl" country;"Germany" pos;49.5435;9.7314;432 board;6.8 firmware;"WT 6.20.2 / 31e" status; 30 distance;71.474188743479 myblitz;N input_board;;;;;; input_firmware;"31e";"31e";"";"";"";"" input_gain;7.7;7.7;7.7;7.7;7.7;7.7 input_antenna;10;10;;;; last_signal;"2012-02-10 13:39:47" signals;3133 last_stroke;"2013-10-04 21:03:34" strokes;0;0;0;4;6;66.6667;752;3983;18.8802'

        station = self.builder.from_line(line).build()

        assert_that(station.number).is_equal_to(364)
        assert_that(station.user).is_equal_to(1)
        assert_that(station.name).is_equal_to(u'Musterdörfl')
        assert_that(station.country).is_equal_to('Germany')
        assert_that(station.x).is_equal_to(9.7314)
        assert_that(station.y).is_equal_to(49.5435)
        assert_that(station.timestamp).is_equal_to(Timestamp("2012-02-10 13:39:47"))
        assert_that(station.board).is_equal_to(u'6.8')

    def test_build_station_offline(self):
        self.builder.set_number(364)
        self.builder.set_user(10)
        self.builder.set_name(u'Musterdörfl')
        self.builder.set_country(u'Germany')
        self.builder.set_x(9.7314)
        self.builder.set_y(49.5435)
        self.builder.set_timestamp("2012-02-10 14:39:47.410492123")
        self.builder.set_status('A')
        self.builder.set_board('0815')

        station = self.builder.build()

        assert_that(station.number).is_equal_to(364)
        assert_that(station.name).is_equal_to(u'Musterdörfl')
        assert_that(station.country).is_equal_to(u'Germany')
        assert_that(station.x).is_equal_to(9.7314)
        assert_that(station.y).is_equal_to(49.5435)
        assert_that(station.timestamp.datetime).is_equal_to(self.get_timestamp("2012-02-10 14:39:47.410492"))
        assert_that(station.timestamp.nanosecond).is_equal_to(123)
        assert_that(station.status).is_equal_to('A')
        assert_that(station.board).is_equal_to('0815')


class StationOffline(object):
    def setUp(self):
        self.builder = blitzortung.builder.StationOffline()

    def test_default_values(self):
        assert_that(self.builder.id_value).is_equal_to(-1)
        assert_that(self.builder.number).is_equal_to(-1)
        assert_that(self.builder.begin).is_none()
        assert_that(self.builder.end).is_none()

    def test_build_station_offline(self):
        self.builder.set_id(364)
        self.builder.set_number(123)

        end = datetime.datetime.utcnow() - datetime.timedelta(hours=1)
        begin = end - datetime.timedelta(hours=1)
        self.builder.set_begin(begin)
        self.builder.set_end(end)

        station_offline = self.builder.build()

        assert_that(station_offline.id).is_equal_to(364)
        assert_that(station_offline.number).is_equal_to(123)
        assert_that(station_offline.begin).is_equal_to(begin)
        assert_that(station_offline.end).is_equal_to(end)


class RawWaveformEventTest(object):
    def setUp(self):
        self.builder = blitzortung.builder.RawWaveformEvent(blitzortung.builder.ChannelWaveform())

    def test_build_raw_green_event(self):
        line = "2013-09-28 20:00:54.490994382 48.000000 11.000000 500 0 GREEN 0 0.0 256 0 8 0 0 1950  1 GREEN 0 0.0 256 0 8 0 0 1950 "
        self.builder.from_string(line)
        raw_event = self.builder.build()

        assert_that(raw_event.x).is_equal_to(11.0)
        assert_that(raw_event.y).is_equal_to(48.0)
        assert_that(raw_event.altitude).is_equal_to(500)

        channels = raw_event.channels
        assert_that(len(channels)).is_equal_to(2)
        assert_that(channels[0].channel_number).is_equal_to(0)
        assert_that(channels[1].channel_number).is_equal_to(1)

    def test_build_raw_red_event(self):
        line = "2013-09-28 19:31:30.913939699 48.500000 11.500000 388 0 12.2 3 8.8 512 128 8 2 1618 1618  1 12.2 3 8.8 512 128 8 4 1618 1618 "
        self.builder.from_string(line)
        raw_event = self.builder.build()

        assert_that(raw_event.x).is_equal_to(11.5)
        assert_that(raw_event.y).is_equal_to(48.5)
        assert_that(raw_event.altitude).is_equal_to(388)

        channels = raw_event.channels
        assert_that(len(channels)).is_equal_to(2)
        assert_that(channels[0].channel_number).is_equal_to(0)
        assert_that(channels[1].channel_number).is_equal_to(1)

    def test_build_with_missing_bits_value(self):
        line = "2014-09-14 19:52:30.507001245 48.500000 11.500000 59 0 12.3 0 16.4 512 256 0 4 952 1904  1 12.3 0 10.5 512 256 0 4 952 1904  3 13.1 0 10.8 512 256 0 4 952 1904  4 13.1 0 10.4 512 256 0 4 952 1904  5 13.1 0 10.5 512 256 0 4 952 1904 "
        self.builder.from_string(line)
        raw_event = self.builder.build()

        channels = raw_event.channels
        assert_that(len(channels)).is_equal_to(5)
        assert_that(channels[0].bits).is_equal_to(8)
        assert_that(channels[1].bits).is_equal_to(8)


class ChannelWaveformTest(object):
    def setUp(self):
        self.builder = blitzortung.builder.ChannelWaveform()

    def test_build_raw_green_event(self):
        line = "0 GREEN 0 0.0 256 0 8 0 0 1950 "
        self.builder.from_field_iterator(iter(line.split(" ")))
        channel_waveform = self.builder.build()

        assert_that(channel_waveform.channel_number).is_equal_to(0)
        assert_that(channel_waveform.amplifier_version).is_equal_to("GREEN")
        assert_that(channel_waveform.antenna).is_equal_to(0)
        assert_that(channel_waveform.gain).is_equal_to("0.0")
        assert_that(channel_waveform.values).is_equal_to(256)
        assert_that(channel_waveform.start).is_equal_to(0)
        assert_that(channel_waveform.bits).is_equal_to(8)
        assert_that(channel_waveform.shift).is_equal_to(0)
        assert_that(channel_waveform.conversion_gap).is_equal_to(0)
        assert_that(channel_waveform.conversion_time).is_equal_to(1950)

        waveform = channel_waveform.waveform
        assert_that(len(waveform)).is_equal_to(256)
        assert_that(waveform[0]).is_equal_to(1)
        assert_that(waveform[1]).is_equal_to(0)
        assert_that(waveform[2]).is_equal_to(-1)

    def test_build_raw_red_event(self):
        line = "1 12.2 3 8.8 512 128 8 4 1618 1618 "
        self.builder.from_field_iterator(iter(line.split(" ")))
        channel_waveform = self.builder.build()

        assert_that(channel_waveform.channel_number).is_equal_to(1)
        assert_that(channel_waveform.amplifier_version).is_equal_to("12.2")
        assert_that(channel_waveform.antenna).is_equal_to(3)
        assert_that(channel_waveform.gain).is_equal_to("8.8")
        assert_that(channel_waveform.values).is_equal_to(512)
        assert_that(channel_waveform.start).is_equal_to(128)
        assert_that(channel_waveform.bits).is_equal_to(8)
        assert_that(channel_waveform.shift).is_equal_to(4)
        assert_that(channel_waveform.conversion_gap).is_equal_to(1618)
        assert_that(channel_waveform.conversion_time).is_equal_to(1618)

        waveform = channel_waveform.waveform
        assert_that(len(waveform)).is_equal_to(512)
        assert_that(waveform[0]).is_equal_to(127)
        assert_that(waveform[1]).is_equal_to(0)
        assert_that(waveform[2]).is_equal_to(-128)
